import React,{useState,useEffect} from 'react';
import axios from 'axios';

import { connect } from 'react-redux';
import Picture from './Picture';
import { getAll, deleteCollection} from '../actions/collectionActions';

const Collection =() => {
  const [pictureData,setPictureData] = useState([]);
  useEffect(()=>{
    axios.get(`https://63ba0e3156043ab3c794e612.mockapi.io/c1`)
      .then((getAllCollections)=>{
        setPictureData(getAllCollections.data)
        console.log(pictureData);
      })
  })
  const deletePicture = (currentPicture) =>{
    axios.delete(`https://63ba0e3156043ab3c794e612.mockapi.io/c1`,{
      currentPicture
  });
  }

  return(
    <>
   <div className="pictures-container">
   {pictureData.map(image=>{
    return(
        <a key={image.currentPicture.id} href={image.currentPicture.previewURL} className="picture">
        <div className="image-wrapper">
          <img className="image" src={image.currentPicture.previewURL}
          alt={`${image.currentPicture.user} image`} />
          <button onClick={()=>deletePicture(image.currentPicture)}
          >Delete</button>
        </div>
        </a>
     )
     })
    }
   </div>
   </>
)}

export default Collection;
