import React,{useState,useEffect} from 'react';
import ReactPaginate from "react-paginate";

export default function Picture( {pictures} ) {
  const [currentItems,setCurrentItems] = useState([]);
  const [pageCount,setPageCount] = useState(0);
  const [itemOffset,setItemOffset] = useState(0);
  const itemPerPage = 2;

  useEffect(()=>{
    const endOffset = itemOffset + itemPerPage;
    setCurrentItems(pictures.slice(itemOffset,endOffset));
    setPageCount(Math.ceil(pictures.length / itemPerPage));
  },[itemOffset,itemPerPage,pictures]);

  const handlePageClick = (event)=>{
    const newOffset = (event.selected* itemPerPage)% pictures.length;
    setItemOffset(newOffset);
  }

 if (Object.keys(currentItems).length && currentItems.length){
  return(
  <>
   <div className="pictures-container">
    {pictures.map(picture=>{
      return(
        <a key={picture.id} href={picture.previewURL} className="picture">
        <div className="image-wrapper">
        <img className="image" src={picture.previewURL} alt={`${picture.user} image`}/>
        </div>
        </a>
     );
     })
    }
   </div>
   <ReactPaginate
    breakLabel="..."
    nextLabel="next >"
    onPageChange={handlePageClick}
    pageRangeDisplayed={3}
    pageCount={pageCount}
    previousLabel="< previous"
    renderOnZeroPageCount={null}
    containerClassName="pagination"
    pageLinkClassName='page-num'
    previousLinkClassName="page-num"
    nextLinkClassName="page-num"
    activeLinkClassName="active"
    />
    </>
  );
 }
}
//export default Picture;
