import {useForm,SubmitHandler} from 'react-hook-form';
import React from 'react';


function SearchInputForm () {

const {register,setError,formState: { errors },handleSubmit,reset} = useForm();

const onFormSubmit = (searchtitle) => {
  console.log(searchtitle);
  //handleSubmit(onFormSubmit(data));
  this.props.onSubmit(searchtitle);
  reset({
     searchtitle: '',
   })
   return false;
};
    return (
      <div className='flex'>
      <form className='search' onSubmit={handleSubmit(onFormSubmit)}>
      <div className="field">
      <input
          type='text'
          placeholder='Search Pictures...'
         {...register('searchtitle',{ required: 'Title is required', maxLength: 30 })}
      />
      {errors.searchtitle && errors.searchtitle.type === "required" && <span>This is required</span>}
      </div>
          <button type='submit'>
            <i className='search icon'></i>
          </button>
          </form>
      </div>
    )
  }
export default SearchInputForm;
