import React from 'react';
import {connect} from 'react-redux';
import {INPUT_CHANGE,SEARCHFORM_SUBMIT}from '../actions/actionTypes'

const SearchInputForm = (props)=>{
  let {inputChange,onFormSubmit,placeholder,title,error} = props
  return (
    <form className='search' onSubmit={onFormSubmit}>
      <div className='flex'>
        <input
          id='title'
          className={ props.error ? 'error' : '' }
          name='title'
          type='text'
          value={title}
          placeholder={placeholder}
          onChange={inputChange}
        />
        <button type='submit'>
          <i className='search icon'></i>
        </button>
      </div>
      <p className='error'>{ error }</p>
    </form>
  )
}
/*Search input title change action*/
const stateToProps = (state)=>{
  return{
    title:state.title,
    error:state.error
     //Get title input state from store
  }
}

const dispatchToProps = (dispatch)=>{
  return {
    inputChange(e){
      let action = {
        type: INPUT_CHANGE,
       title: e.target.value
      }
      dispatch(action)
    },
    onFormSubmit(title) {
      let action = {
        type: SEARCHFORM_SUBMIT,
        title:title
      }
        dispatch(action)
        console.log('888')
    }
  }
}

export default connect(stateToProps,dispatchToProps)(SearchInputForm);
