import React,{useState} from 'react';
import { useSelector, useDispatch} from 'react-redux';
import Picture from './Picture';
import SearchInputForm from './SearchInputForm';
import {search} from '../actions/searchActions';
import { connect } from 'react-redux';

function Search(){

  const searchStatus = useSelector(state => state.status.search);
  const searchError = useSelector(state => state.status.searchError);
  const result = useSelector(state => state.search)
  const dispatch = useDispatch();
  const title = useSelector(state =>state.title);
  const [pictures,setPictures] = useState([]);
  setPictures(result.data)
  const submitSearch = (title)=>{
    dispatch(search(title));
  }

  return(
  <main id='search-container'>
    <SearchInputForm
      placeholder='Search Pictures...'
      onSubmit={ (title) => dispatch(search(title)) }
      //search={submitSearch}  //send search action
    />

    {
      (searchStatus === 'SUCCESS')

        ? <Picture
          title='red rose'
          image src=''
          />
      : null
    }


    {
      (searchStatus === 'PENDING')
      ? <section className='loading'>
          <img src='../../images/loading.gif' alt="On loading"/>
        </section>
      : null
    }
    {
      (searchStatus === 'ERROR')
      ? <section className='error'>
          <p className='error'>
            <i className="red exclamation triangle icon"></i>
            { searchError }
          </p>
        </section>
      : null
    }

  </main>
)
};

export default connect()(Search);
