import {useForm} from 'react-hook-form';

function SearchInputForm () {
const {register,setValue,setError,formState: { errors },handleSubmit,reset} = useForm();

const onSubmit = (data, e) => {
   console.log('datatitle',data.searchtitle);
   e.target.reset(); // reset after form submit
   reset({
     searchtitle: '',
   })
    return false;
  };
    return (
      <div className='flex'>
      <form className='search' onSubmit={handleSubmit(onSubmit)}>
      <div className="field">
      <input
          type='text'
          placeholder='Search Pictures...'
         {...register('searchtitle', { required: 'Title is required', maxLength: 30 })}
      />
      {errors.searchtitle && errors.searchtitle.type === "required" && <span>This is required</span>}
      </div>
          <button type='submit'>
            <i className='search icon'></i>
          </button>
          </form>
      </div>
    )
  }
export default SearchInputForm;
