import apiClient from '../apiClient';
import {SEARCH_INPUT_CHANGE,SEARCH_REQUEST,SEARCH_SUCCESS,SEARCH_FAILURE} from './actionTypes'

export const searchInputChange = (event) => ({
    type: 'SEARCH_INPUT_CHANGE',
    value: event.target.value
})
export const onFormSubmit = () => ({
    type: 'ON_FORM_SUBMIT'
})

export const searchRequest = () => ({
  type: 'SEARCH_REQUEST'
});

export const searchSuccess = (result) => ({
  type: 'SEARCH_SUCCESS', result
});

export const searchFailure = (error) => ({
  type: 'SEARCH_FAILURE', error
});

export function search(title){
  return (dispatch) => {
    dispatch(searchRequest());

    apiClient.query(title)
      .then(response => {
        dispatch(searchSuccess(response.data))
      })
      .catch(error => {
        dispatch(searchFailure(error.response.data))
      });
  }
}
