import React from 'react';
import {connect} from 'react-redux'
import { searchInputChange,searchRequest } from '../actions/searchActions';

  const SearchInputForm = (props)=>{
  let {title,searchInputChange,onFormSubmit,placeholder} = props
  return(
    <form className='search' onSubmit={onFormSubmit}>
      <div className='flex'>
        <input
          id='title'
          //className={ this.state.error ? 'error' : '' }
          name='title'
          type='text'
          value={title}
          placeholder={placeholder}
          onChange={searchInputChange}
        />
        <button type='submit'>
          <i className='search icon'></i>
        </button>
      </div>
    </form>
  )
}
const stateToProps = (state)=>{
  return{
    title:state.title,
  }
}

  const dispatchToProps = (dispatch)=>{
    return{
     searchInputChange: () => dispatch(searchInputChange),
     onFormSubmit:()=> dispatch(searchRequest),

    }
 }

export default connect(stateToProps,dispatchToProps)(SearchInputForm);
