)}
console.log('errors',errors);
}

useEffect(() => {
    register("searchtitle", { required: true});
  }, []);
    return (
      <div className='flex'>
      <pre>{JSON.stringify(searchtitle,undefined,2)}</pre>
      <form className='search' onSubmit={handleSubmit(onSubmit)}>
      <div className="field">
