import {useSelector,useDispatch} from 'react-redux';
import Picture from './Picture';

const SearchInputForm = ()=>  {

    const [search,setSearch] = useState('');
    const [errors,setErrors] = useState({});
  //const [query,setQuery] = useState('yellow flower');
  // var URL = "https://pixabay.com/api/?key="+API_KEY+"&q="+encodeURIComponent(query);

    const inputChange = (event) => {
      const title  = event.target.value;
      setSearch(title);
      console.log(title);
    }

   const onFormSubmit = (event) => {
    event.preventDefault();
    setErrors(validate(search));
  }
    const validate = (value) => {
     const errors = {};
     if (value.trim().length === 0) return 'Title is required!';
    return errors;
      }

    return (
      <div className='flex'>
      <form className='search' onSubmit={onFormSubmit}>
      <input
            id='title'
            //className={ this.state.error ? 'error' : '' }
            name='title'
            type='text'
            value={search}
            placeholder='Search Pictures...'
            onChange={inputChange}
          />
          <button type='submit'>
            <i className='search icon'></i>
          </button>
      </form>

        <Picture
         />  //image link from Pixabay data

      </div>
    )
  }

export default SearchInputForm;
