import {createStore,combineReducers,applyMiddleware} from 'redux'
import search from './reducers/searchReducer';
import collections from './reducers/collectionsReducer';
import thunk from "redux-thunk";
import status from './reducers/stautusReducer';
//import ReduxPromise from "redux-promise";  //add for devtools
import { composeWithDevTools } from 'redux-devtools-extension';

const store = createStore(combineReducers({search,collections,status}),{},
composeWithDevTools(applyMiddleware(thunk),))

export default store
