import React,{useState,useEffect} from 'react';
import Picture from './Picture';
import axios from 'axios';

const SearchInputForm = ()=>  {
    var API_KEY = '14204379-7d3ac6eed48661065b4c023bb';
    var URL = "https://pixabay.com/api/?key="+API_KEY+"&q="+encodeURIComponent('red roses');
    const getPictures = async()=>{
      const res = await fetch(URL);
      const data = await res.json();
      setPictures(data.hits)
      console.log(data.hits);  //Pass display res
    }

    const [search,setSearch] = useState('');
    const [errors,setErrors] = useState({});
    const [pictures,setPictures] = useState([]);

     useEffect(()=>{
       getPictures()
     },[search]);

    const inputChange = (event) => {
      const title  = event.target.value;
      setSearch(title);
      console.log(title);
  }

   const onFormSubmit = (event) => {
    event.preventDefault();
    setErrors(validate(search));
   }
    const validate = (value) => {
     const errors = {};
     if (value.trim().length === 0) return 'Title is required!';
    return errors;
  }

    return (
      <div className='flex'>
      <form className='search' onSubmit={onFormSubmit}>

          <input
            id='title'
            //className={ this.state.error ? 'error' : '' }
            name='title'
            type='text'
            value={search}
            placeholder='Title'
            onChange={inputChange}
          />
          <button type='submit'>
            <i className='search icon'></i>
          </button>
      </form>
      {pictures.map(picture =>(
        <Picture
        title={picture.tags}
        image={picture.previewURL}
          />
      ))}
      </div>
    )
  }

export default SearchInputForm;
