import React,{useState} from 'react';
import {useForm} from 'react-hook-form';

const SearchInputForm = ({search})=>  {
    const {register,handleSubmit}=useForm();
    const [searchtitle,setSearchtitle] = useState('');
    const [errors,setErrors] = useState({});

    const inputChange = (event) => {
      const title  = event.target.value;
      setSearchtitle(title);
      console.log(title);
    }

  /* const onSubmit = (data) => {  //
    //event.preventDefault();
  //  setErrors(validate(searchtitle));
    setSearchtitle(data);
    console.log(data)
  }*/

  const onFormSubmit = (event) => {  //
   event.preventDefault();
   setErrors(validate(searchtitle));
   search(searchtitle);
   console.log(searchtitle)
 }
    const validate = (value) => {
     const errors = {};
     if (value.trim().length === 0) return 'Title is required!';
    return errors;
  }

    return (
      <div className='flex'>
      <pre>{JSON.stringfy(searchtitle,undefined,2)}</pre>
      <form className='search' onSubmit={handleSubmit(onFormSubmit)}>
      <input
            id='title'
            //className={ this.state.error ? 'error' : '' }
            name='title'
            type='text'
            value={searchtitle}
            placeholder='Search Pictures...'
            ref = {register({required:'Title is required'})}
            onChange={inputChange}
          />
            <p>{errors.title?.message} </p>
          <button type='submit'>
            <i className='search icon'></i>
          </button>
      </form>
      </div>

    )
  }

export default SearchInputForm;
