import React from 'react';
import {BrowserRouter as Router,Route,Routes,Link} from 'react-router-dom';
import Search from './Pages/Search';
import Collections from './Pages/Collections'
//import './css/main.css';

function App(){


    const pathname = window.location.pathname;

  return(
    /*<Route path="/" exact component={Index} />
      <Route path="/favorites/" component={Favorites} />／这个版本没有Routes包裹不行 */

    <Router>

    <div id='app'>
    <header> Pixabay Picture Search</header>
    <nav>
     <Link to='/'
              className={ pathname === '/' ? 'active' : '' }
     >Search
     </Link>
     <Link to='/collections'
      className={ pathname === '/collections' ? 'active' : '' }
     >Collection
     </Link>
     </nav>
    <Routes>
    <Route path='/' exact element={<Search/>} />
    <Route path='/collections' element={<Collections/>}/>
    </Routes>

    <Route exact path='/' render={() => (
            <Search
              /*handleFavoriteButtonClicked={
                () => this.setState({ showAddFavoriteForm: true })
              }*/
            />
          )} />
    <Route exact path='/collections' render={() => (
            <Collections />
          )} />



    </div>
  </Router>

  )

  }

export default App
