import axios from 'axios';

var API_KEY = '14204379-7d3ac6eed48661065b4c023bb';
const SEARCH_URL = (title) => "https://pixabay.com/api/?key="+API_KEY+"&q=" + encodeURIComponent(title);
//var URL = "https://pixabay.com/api/?key="+API_KEY+"&q="+encodeURIComponent(title);

const COLLECTS_URL = '/api/collects';
const COLLECT_URL = (imdbID) => '/api/collects/' + imdbID;
