import {SEARCH_INPUT_CHANGE,SEARCHFORM_SUBMIT}from '../actions/actionTypes'

const defaultState = {
  title : '',
  error:''
}

export default (state = defaultState, action)=>{
  if (action.type==='SEARCH_SUCCESS'){
      state = action.result;
    }
  return state
}
