const [searchtitle,setSearchtitle]=useState('');
useEffect(() => {
    setSearchtitle('')
  }, [searchtitle])

const onSubmit = (data, e) => {
    e.target.reset(); // reset after form submit
  searchtitle.reset();
   console.log('datatitle',data.searchtitle);
  };
