import React,{useState,useEffect} from 'react';
import {useForm} from 'react-hook-form';

function SearchInputForm () {
const {register,setValue,setError,formState: { errors },handleSubmit} = useForm();
const [searchtitle,setSearchtitle]=useState();
register('test')
register('test1.data')
const onSubmit= (data) => {
 setSearchtitle(data);
 console.log(searchtitle);
console.log('errors',errors.message);
}

useEffect(() => {
    register('searchtitle', { required:{
      value:true,
      message:"This is Required" }});
  }, []);
    return (
      <div className='flex'>

      <form className='search' onSubmit={handleSubmit(onSubmit)}>
      <div className="field">
      <input
          type='text'
          placeholder='Search Pictures...'
          name='searchtitle'{...register('searchtitle', { required: true, maxLength: 30 })} />
      {errors.searchtitle && errors.searchtitle.type === "required" && <span>This is required</span>}

          onChange={e => {
          const value = e.target.value;
          if (value === " ") {
            setError('searchtitle', 'Required')
                  } else {
                    setValue("searchtitle", e.target.value)
                  }
        }}
        />
      </div>
          <button type='submit'>
            <i className='search icon'></i>
          </button>
          </form>
      </div>
    )
  }
export default SearchInputForm;
