import React,{useState,useEffect} from 'react';
import {searchInputChange} from '../actions/searchActions';
import axios from 'axios';

function SearchInputForm (){

    var API_KEY = '14204379-7d3ac6eed48661065b4c023bb';
    var URL = "https://pixabay.com/api/?key="+API_KEY+"&q="+encodeURIComponent('red roses');
    const res = axios.get(URL);
    const data=res.data;
    //  console.log(res);  //Pass display res after Collections page diaplay

    const [search,setSearch] = useState('');
    const [errors,setErrors] = useState({});

     useEffect(()=>{
       console.log(search);
     },[search]);

    const inputChange = (event) => {
      const search  = event.target.value;
      setSearch(search);
      console.log(search);
  }

  const onFormSubmit = (event) => {
    event.preventDefault();
    setErrors(validate(search));

  }

    const validate = (value) => {
     const errors = {};
     if (value.trim().length === 0) return 'Title is required!';
    return errors;
  }

    return (
      <form className='search' onSubmit={onFormSubmit}>
        <div className='flex'>
          <input
            id='title'
            //className={ this.state.error ? 'error' : '' }
            name='title'
            type='text'
            value={search.title}
            placeholder='Title'
            onChange={inputChange}
          />
          <button type='submit'>
            <i className='search icon'></i>
          </button>
        </div>

      </form>
    )
  }

export default SearchInputForm;
