import React,{useState,useEffect} from 'react';
import {searchInputChange} from '../actions/searchActions';
import axios from 'axios';

function SearchInputForm (){

      var API_KEY = '14204379-7d3ac6eed48661065b4c023bb';
      var URL = "https://pixabay.com/api/?key="+API_KEY+"&q="+encodeURIComponent('red roses');
      const res = axios.get(URL);
      const data=res.data;
      console.log(res);  //Pass display res after Collections page diaplay

      const [search,setSearch] = useState('');
     useEffect(()=>{
       console.log(search);
     },[search]);
    /*const getSearchResult = async()=>{
      const res = axios.get(URL);
      const data=res.json()
      console.log(data);
    }*/

    const inputChange = (event) => {
      search: event.target.value;
      setSearch(search);
      console.log(search);
  }

  const onFormSubmit = (event) => {
    event.preventDefault();
    const title = this.state.title;
    const error = this.validate(title);
    this.setState({ error });

    if (error.length > 0) return;
    this.props.onSubmit(title);
    this.setState({ title: '', error: '' });
  }

    const validate = (title) => {
    if (title.trim().length === 0) return 'Title is required!';
    return '';
  }

    return (
      <form className='search' onSubmit={onFormSubmit}>
        <div className='flex'>
          <input
            id='title'
            //className={ this.state.error ? 'error' : '' }
            name='title'
            type='text'
            value={search}
            placeholder={title}
            onChange={inputChange}
          />
          <button type='submit'>
            <i className='search icon'></i>
          </button>
        </div>

      </form>
    )
  }

export default SearchInputForm;
