      const data = await res.json();
      setPictures(data.hits)  //Pass display data.hits
      console.log(data.hits);
    }*/

 const apiClient = {
  query(title) {
    //title = encodeURIComponent(title);
    const res = fetch(SEARCH_URL); //axios not work,get search result
    const data = res.json();
    console.log(data.hits);
  },

  /*query(title) {
      title = encodeURIComponent(title);
      return axios.get(SEARCH_URL(title));
    },*/
  saveCollect(movie) {
    return axios.post(COLLECTS_URL, movie);
  },

  getAllCollects() {
    return axios.get(COLLECTS_URL);
  },


  deleteCollect(imdbID) {
    return axios.delete(COLLECT_URL(imdbID));
  },
}

window.apiClient = apiClient;
window.axios = axios;

export default apiClient;
