import React from 'react';

import { connect } from 'react-redux';
import Picture from './Picture';
import { getAll, deleteCollection} from '../actions/collectionActions';

const Collection =(props) => {
  return(
  <main id='collections-container'>
    <ul>
    {
      Object.keys(props.collections).map(pictureID => (
        <li key={pictureID}>
          <Picture
            picture={ props.collections[pictureID] }
            isCollection={true}
            editable={true}
            onDelete={ () => deleteCollection(pictureID) }
          />
        </li>
      ))
    }
    {
      (Object.keys(props.collections).length === 0)
        ? <p className='message'>Collections list is empty.</p>
        : null
    }
    </ul>
  </main>
)
}

  const mapStateToProps = (state) => (
  { collections: state.collections }
  )
  const mapDispatchToProps = (dispatch) => (
  {
    getAll(){
      dispatch(getAll())
    },
    deleteCollection(pictureID) {
      dispatch(deleteCollection(pictureID))
    }
  }
)
export default connect(mapStateToProps, mapDispatchToProps)(Collection);
