import React,{useState,useEffect} from 'react';
import {useForm} from 'react-hook-form';

function SearchInputForm () {
const {register,handleSubmit,setValue, setError} = useForm();
const [searchtitle,setSearchtitle]=useState();

const onSubmit= (data) => {
 setSearchtitle(data);
 console.log(searchtitle);
}
useEffect(() => {
    register("searchtitle", { required: 'Title is required' });
  }, []);
    return (
      <div className='flex'>
      <pre>{JSON.stringify(searchtitle,undefined,2)}</pre>
      <form className='search' onSubmit={handleSubmit(onSubmit)}>
      <div className="field">
      <input
            type='text'
            placeholder='Search Pictures...'

            name='searchtitle'
            onChange={e => {
          const value = e.target.value;
          if (value === "") {
            setError("searchtitle", "Required")
          } else {
            setValue("searchtitle", e.target.value)
          }
        }}
          />

      </div>
          <button type='submit'>
            <i className='search icon'></i>
          </button>
      </form>
      </div>
    )
  }
export default SearchInputForm;
