          editable={ false }
        />
      : null
    }
    {
      (searchStatus === 'PENDING')
      ? <section className='loading'>
          <img src='../../images/loading.gif' alt="On loading"/>
        </section>
      : null
    }
    {
      (searchStatus === 'ERROR')
      ? <section className='error'>
          <p className='error'>
            <i className="red exclamation triangle icon"></i>
            { searchError }
          </p>
        </section>
      : null
    }
  </main>
)
};

export default Search;
