import React, {Component} from 'react';
import 'antd/dist/antd.css'
import {Input,Button,List} from 'antd'  //import antDesign 3 components
import store from './store'    //webpack support index.js can be cut without write on


class TodoList extends Component {
  constructor(props){
    super(props)
    //console.log(store.getState())
    this.state=store.getState()
    this.changeInputValue= this.changeInputValue.bind(this)
    this.storeChange= this.storeChange.bind(this)
    store.subscribe(this.storeChange)
  }

  render(){
    return(
    <div style={{margin:'10px'}}>
      <div>
        <Input
        placeholder={this.state.inputValue}
        style={{width:'250px',marginRight:'10px'}}
        onChange={this.changeInputValue}
        />
        <Button type="primary">Increase</Button>
      </div>

      <div style={{margin:'10px',width:'300px'}}>
        <List
          bordered
          dataSource={this.state.list}
          renderItem={item=>(<List.Item>{item}</List.Item>)}  //ant design paten
          />

      </div>
    </div>
    );
  }

    changeInputValue(e){
      const action = {
        type:'changeInput',
        value:e.target.value
      }//console.log(e.target.value)
      store.dispatch(action)
    }
    storeChange(){
      this.setState(store.getState())
    }
}
export default TodoList;
