import React, {Component} from 'react';
import 'antd/dist/antd.css'
import {Input,Button,List} from 'antd'  //import antDesign 3 components
import store from './store'    //webpack support index.js can be cut without write on


class TodoList extends Component {
  constructor(props){
    super(props)
    //console.log(store.getState())
    this.state=store.getState()
    this.changeInputValue= this.changeInputValue.bind(this)
    this.storeChange= this.storeChange.bind(this)
    this.clickBtn= this.clickBtn.bind(this) //After clickBtn in Button Bind here
    store.subscribe(this.storeChange)
  }

  render(){
    return(
    <div style={{margin:'10px'}}>
      <div>
        <Input
        placeholder={this.state.inputValue}
        style={{width:'250px',marginRight:'10px'}}
        onChange={this.changeInputValue} //我们执行一个方法
        value={this.state.inputValue}
        />
        <Button
        type="primary"
        onClick={this.clickBtn}
        >Increase</Button>
      </div>

      <div style={{margin:'10px',width:'300px'}}>
        <List
          bordered
          dataSource={this.state.list}
          renderItem={(item,index)=>(<List.Item onClick={this.deleteItem.bind(this,index)}>{item}</List.Item>)}  //ant design paten
          />

      </div>
    </div>
    );
  }

    changeInputValue(e){
      const action = {
        type:'changeaInput',
        value:e.target.value
      }//console.log(e.target.value)
      store.dispatch(action)
    }
    storeChange(){
      this.setState(store.getState())
    }

    clickBtn(){
      const action={type:'addItem'}
      store.dispatch(action)
    }

    deleteItem(index){
      const action={
        type:'deleteItem',
        index
      }
      store.dispatch(action)
    }
}
export default TodoList;
