import React, {Component} from 'react';
import 'antd/dist/antd.css'
import {Input,Button,List} from 'antd'  //import antDesign 3 components

class TodoListUI extends Component {
render(){
  return(
  <div style={{margin:'10px'}}>
    <div>
      <Input
      placeholder={this.props.inputValue}
      style={{width:'250px',marginRight:'10px'}}
      onChange={this.props.changeInputValue} //我们执行一个方法
      inputValue={this.props.inputValue}
      />
      <Button
      type="primary"
      onClick={this.props.clickBtn}
      >Increase</Button>
    </div>

    <div style={{margin:'10px',width:'300px'}}>
      <List
        bordered
        dataSource={this.props.list}  //change all the state to props
        renderItem={(item,index)=>(<List.Item onClick={(index)=>{this.props.deleteItem(index)}}>{item}</List.Item>)}  //ant design paten
        />

    </div>
  </div>
  );
}
}
export default TodoListUI;
