import React, { Component } from 'react';
import { connect } from 'react-redux';
import Update from '../components/Update';

class UpdateContainer extends Component {

  state = {
   title : this.props.skill.title,
   message : this.props.skill.message
  }

  handleEdit = event => {
     const { name, value } = event.target
     this.setState({ [name]: value })
   }

  handleUpdate = (e) => {
   e.preventDefault();
  const newTitle = this.getTitle.value;
   const newMessage = this.getMessage.value;
    const data = {

    newTitle,
    newMessage
   }
  this.props.dispatch({ type: 'UPDATE', id: this.props.skill.id, data: data })
 }

render() {
const { title, message } = this.state
return (
<div className="skill" >
<Update
  title={title}
  message={message}

  handleClick={this.handleUpdate}
  handleChange={this.handleEdit} />
</div>
) }
}

export default connect()(UpdateContainer);
