import React, { Component } from 'react';
import { update } from '../actions';
import { connect } from 'react-redux';
import Update from '../components/Update';

class UpdateContainer extends Component {

  state = {
   title : this.props.skill.title,
   message : this.props.skill.message
  }

  handleEdit = event => {
     const { name, value } = event.target
     this.setState({ [name]: value })
   }



render() {
const { title, message } = this.state
return (
<div className="skill" >
<Update
  title={title}
  message={message}

  handleSubmit={this.handleUpdate}
  handleChange={this.handleEdit} />
</div>
) }
}

const mapDispatchToProps = (dispatch, ownProps) => ({
  handleUpdate: (event) => dispatch((_, getState) => { // <-- dispatch a thunk
    event.preventDefault()
    const { newTitle, newMessage } = this.state
     const data = {
     id: this.props.skill.id,
     newTitle,
     newMessage
    }
    dispatch(update(data))
  }
)
})

export default connect(mapDispatchToProps)(UpdateContainer);
