import React from 'react';
const Update = props =>(
<div key={props.skill.id} className="post">
<form className="form" onSubmit={props.handleUpdate}>
    <input required type="text"
    defaultValue={this.props.skill.title}
    onChange={props.handleEdit}
    name="title"
    placeholder="Enter Skill Title" /><br /><br />
    <textarea required rows="5"
    defaultValue={this.props.skill.message} cols="28"
    onChange={props.handleEdit}
    name="message"
    placeholder="Enter Skill Detail" /><br /><br />
    <button>Update</button>
</form>
</div>
);

export default Update;
