import React, { Component } from 'react';
import { updateSkill } from '../actions';
import { connect } from 'react-redux';
import Update from '../components/Update';


class UpdateContainer extends Component {

  state = {
   title : this.props.skill.title,
   message : this.props.skill.message
  }

  handleEdit = event => {
     const { name, value } = event.target
     this.setState({ [name]: value })
   }

handleUpdate = (e) => {
  e.preventDefault();
 const { newTitle, newMessage } = this.state
  const data = {
    id: this.props.skill.id,
    newTitle,
    newMessage,
  }
  this.props.dispatch(updateSkill(data))
}
render() {

const { title, message } = this.state
return (
<div>
   <Update
   title={title}
   message={message}

   handleChange={this.handleEdit}
   handleSubmit={this.handleUpdate} />
</div>
);
}
}
export default connect()(UpdateContainer);
