import React, { Component } from 'react';
import { updateSkill } from '../actions';
import { connect } from 'react-redux';
import Update from '../components/Update';

class UpdateContainer extends Component {

  handleUpdate = event => {
     const { name, value } = event.target
     this.setState({ [name]: value })
   }
handleEdit = (e) => {
  e.preventDefault();
 const { newTitle, newMessage } = this.state
  const data = {
    newTitle,
    newMessage,
  }
  this.props.dispatch(updateSkill(data))
}
render() {
const { title, message } = this.state
return (
  <div key={this.props.skill.id} className="post">
  <form className="form" onSubmit={this.handleEdit}>
   <Update
   title={title}
   message={message}
   handleEdit={this.handleEdit}
   handleChange={this.handleUpdate} />
   </form>
</div>
);
}
}
export default connect()(UpdateContainer);
