import React, { Component } from 'react';
import { update } from '../actions';
import { connect } from 'react-redux';
import Update from '../components/Update';

class UpdateContainer extends Component {

  state = {
   title : this.props.skill.title,
   message : this.props.skill.message
  }

  handleEdit = event => {
     const { name, value } = event.target
     this.setState({ [name]: value })
   }



render() {
const { title, message } = this.state
return (
<div key={this.props.skill.id} className="skill" >
<Update
  title={title}
  message={message}

   handleSubmit={this.handleSubmit}
   handleChange={this.handleEdit} />
</div>
) }
}

const mapDispatchToProps = (
  dispatch,
  ownProps
) => {
  return {
    handleSubmit: () => {
      dispatch({
        type: 'UPDATE',
        id: ownProps.id,
        data: ownProps.data
      });
    }
  };
}
export default connect(mapDispatchToProps)(UpdateContainer);
