import React, { Component } from 'react';
import { connect } from 'react-redux';
import Update from '../components/Update';
import { update } from '../actions';
import { bindActionCreators } from 'redux';
class UpdateContainer extends Component {

  constructor(props) {
    super(props)
  this.state = {
    title : this.props.skill.title,
    message : this.props.skill.message
     }
  }
  handleEdit = event => {
     const { name, value } = event.target
     this.setState({ [name]: value })
   }

  handleUpdate = (e) => {
   e.preventDefault();
   const newTitle = this.state.title.value;
   const newMessage = this.state.message.value;

    const data = {
    id: this.props.skill.id,
    newTitle,
    newMessage
   }
  this.props.dispatch({ type: 'UPDATE', id: this.props.skill.id, data: data })
 }

render() {
const { title, message } = this.state
return (
<div key={this.props.skill.id} className="skill" >
<Update
title={title}
message={message}

  handleClick={this.handleUpdate}
  handleChange={this.handleEdit} />
</div>
) }
}

export default connect()(UpdateContainer);
