import React, { Component } from 'react';
import { updateSkill } from '../actions';
import { connect } from 'react-redux';
import Update from '../components/Update';

class UpdateContainer extends Component {

handleEdit = (e) => {
  e.preventDefault();
  const newTitle = e.target.dataset.getTitle.value;
  const newMessage = e.target.dataset.getMessage.value;
  const data = {
    newTitle,
    newMessage,
  }
  this.props.dispatch(updateSkill(data))
}
render() {
return (
  <div key={this.props.skill.id} className="post">
   <Update
   title={newTitle}
   message={newMessage}
   onSubmit={this.handleEdit}/>
</div>
);
}
}
export default connect()(UpdateContainer);
